import { Img, Container, Selected } from './desingsStyles'

const Designs = () => {
    const [photos, setPhotos] = useState([Shelter, Shelter1])
    const [selectedPhoto, setSelectedPhoto] = useState()
    
    const { activatePhoto, deactivatePhoto } = useContext(PhotoContext)

    
    return (
        <Container>
            {
                photos && photos.map(p => (
                    <Img src={p} onClick={() => handleSelectPhoto(p)} />
                ))
            }

            {
                selectedPhoto && <Selected />
            }
        </Container>
    )
}

export default Designs
