import styled from 'styled-components'


export const Container = styled.div`
    display: flex;
    justify-content: center;
    margin: 1rem 0;
    position: relative;
    flex-wrap: wrap
`

export const Img = styled.img`
    width: 46%;
    margin: 1rem;
    border: 2px solid #cb9b87;

    &:hover {
        cursor: pointer;
        
    }
`   

export const Selected = styled.img`
    position: absolute;
    height: 90vh;
    top: -4rem;
    z-index: 50000;

    @media(max-width: 960px) {
        width: 80%
        height: auto
    }

`
