import { PhotoContext } from '../../context/photoContext'
import Shelter from '../../desingImgs/shelter.jpg'
import Shelter1 from '../../desingImgs/Image.png'
import { Img, Container, Selected } from './desingsStyles'

const Designs = () => {
    const [photos, setPhotos] = useState([Shelter])
    const [selectedPhoto, setSelectedPhoto] = useState()
    cosnt [showImage, setShowImage] = useState(false)

    useEffect(() => {
        if(active) {
            if(active) {
                setTimeout(() => {
                    setShowImage(true)
                }, 2000)
            }
        }else {
            setShowImage(false)
        }
    }, [active])

    const { activatePhoto, deactivatePhoto, active } = useContext(PhotoContext)

    const handleSelectPhoto = photo => {
        activatePhoto()
        setSelectedPhoto(photo)
    }
    return (
        <Container>
            {
                photos && photos.map(p => (
                    <Img src={p} onClick={() => handleSelectPhoto(p)} />
                ))
            }

            {
                selectedPhoto && active && <Selected src={selectedPhoto} showImage={active ? setTimeout(() => {  return true }, 2000) : false}/>
            }
        </Container>
    )
}

export default Designs
